import React, { useState, useRef, useEffect } from 'react';
import { useSpeechToText } from '../hooks/useSpeechToText';
import './VideoInterview.css';

function VideoInterview({ isActive, onStart, onStop, onTranscriptUpdate, onAnswerComplete, canProceed }) {
  const [stream, setStream] = useState(null);
  const [isMicOn, setIsMicOn] = useState(false);
  const [timer, setTimer] = useState(0);
  const [silenceTimer, setSilenceTimer] = useState(0);
  const videoRef = useRef(null);
  const timerIntervalRef = useRef(null);
  const silenceIntervalRef = useRef(null);
  const lastTranscriptLengthRef = useRef(0);

  const {
    transcript,
    isListening,
    startListening,
    stopListening,
    resetTranscript
  } = useSpeechToText();

  useEffect(() => {
    onTranscriptUpdate(transcript);
  }, [transcript, onTranscriptUpdate]);

  // Improved auto-advance logic with silence detection
  useEffect(() => {
    if (isActive && isListening && transcript.trim().length > 0) {
      const currentLength = transcript.trim().length;
      
      // Check if transcript has changed
      if (currentLength !== lastTranscriptLengthRef.current) {
        lastTranscriptLengthRef.current = currentLength;
        setSilenceTimer(0);
      } else {
        // Transcript hasn't changed - increment silence timer
        setSilenceTimer(prev => prev + 1);
      }

      // Auto-advance after 3 seconds of silence and minimum 30 words
      if (silenceTimer >= 3 && currentLength > 100) {
        handleNextQuestion();
      }
    }
  }, [transcript, isActive, isListening, silenceTimer]);

  // Reset silence timer when transcript changes
  useEffect(() => {
    if (transcript.trim().length > 0) {
      lastTranscriptLengthRef.current = transcript.trim().length;
      setSilenceTimer(0);
    }
  }, [transcript]);

  const handleNextQuestion = () => {
    if (transcript.trim().length > 0) {
      stopListening();
      onAnswerComplete(transcript);
      resetTranscript();
      setSilenceTimer(0);
      lastTranscriptLengthRef.current = 0;
      
      // Restart listening after a short delay
      setTimeout(() => {
        if (isActive) {
          startListening();
        }
      }, 1500);
    }
  };

  const startCamera = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true
      });
      setStream(mediaStream);
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
      }
    } catch (error) {
      console.error('Error accessing camera/microphone:', error);
      alert('Please allow camera and microphone access to continue.');
    }
  };

  const stopCamera = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
  };

  const handleStart = () => {
    startCamera();
    startListening();
    setIsMicOn(true);
    onStart();
    
    timerIntervalRef.current = setInterval(() => {
      setTimer(prev => prev + 1);
    }, 1000);
  };

  const handleStop = () => {
    stopCamera();
    stopListening();
    setIsMicOn(false);
    onStop();
    
    if (timerIntervalRef.current) {
      clearInterval(timerIntervalRef.current);
    }
    setTimer(0);
    resetTranscript();
  };

  useEffect(() => {
    return () => {
      stopCamera();
      if (timerIntervalRef.current) {
        clearInterval(timerIntervalRef.current);
      }
    };
  }, []);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="video-interview">
      <div className="video-container">
        <video
          ref={videoRef}
          autoPlay
          playsInline
          muted
          className="video-preview"
        />
        {!stream && (
          <div className="video-placeholder">
            <p>Camera will appear here</p>
          </div>
        )}
      </div>

      <div className="video-controls">
        <div className="control-item">
          <div className={`mic-indicator ${isMicOn && isListening ? 'active' : ''}`}>
            <span className="mic-icon">üé§</span>
            <span>{isMicOn && isListening ? 'Listening...' : 'Mic Off'}</span>
          </div>
        </div>

        <div className="control-item">
          <div className="timer-display">
            <span className="timer-icon">‚è±Ô∏è</span>
            <span>{formatTime(timer)}</span>
          </div>
        </div>

        <div className="control-item">
          {!isActive ? (
            <button className="start-btn" onClick={handleStart}>
              Start Interview
            </button>
          ) : (
            <button className="stop-btn" onClick={handleStop}>
              Stop Interview
            </button>
          )}
        </div>
      </div>

      <div className="transcript-section">
        <div className="transcript-header">
          <h3>Live Transcript</h3>
          {isActive && transcript.trim().length > 0 && (
            <button 
              className="next-question-btn" 
              onClick={handleNextQuestion}
              disabled={!canProceed}
            >
              Next Question ‚Üí
            </button>
          )}
        </div>
        <textarea
          className="transcript-textarea"
          value={transcript}
          readOnly
          placeholder="Your speech will appear here... Speak clearly and naturally."
        />
        {isActive && transcript.trim().length > 0 && (
          <div className="transcript-footer">
            <span className="word-count">{transcript.trim().split(/\s+/).length} words</span>
            {silenceTimer >= 2 && (
              <span className="auto-advance-hint">Auto-advancing in {3 - silenceTimer} seconds...</span>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default VideoInterview;
