import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import VideoInterview from '../components/VideoInterview';
import AIQuestionPanel from '../components/AIQuestionPanel';
import { useInterviewLogic } from '../hooks/useInterviewLogic';
import './InterviewPage.css';

function InterviewPage() {
  const { companyId } = useParams();
  const navigate = useNavigate();
  const {
    currentQuestionIndex,
    questions,
    answers,
    isInterviewActive,
    startInterview,
    stopInterview,
    saveAnswer,
    getResults
  } = useInterviewLogic(companyId);

  const [transcript, setTranscript] = useState('');

  const handleAnswerComplete = useCallback((answerText) => {
    if (answerText.trim().length > 0) {
      saveAnswer(currentQuestionIndex, answerText);
      setTranscript('');
    }
  }, [currentQuestionIndex, saveAnswer]);

  const handleInterviewComplete = useCallback(() => {
    const results = getResults();
    navigate('/result', { state: { results, companyId } });
  }, [getResults, navigate, companyId]);

  useEffect(() => {
    if (currentQuestionIndex >= questions.length && questions.length > 0) {
      stopInterview();
      setTimeout(() => {
        handleInterviewComplete();
      }, 1000);
    }
  }, [currentQuestionIndex, questions.length, stopInterview, handleInterviewComplete]);

  return (
    <div className="interview-page">
      <div className="interview-container">
        <div className="interview-left">
          <VideoInterview
            isActive={isInterviewActive}
            onStart={startInterview}
            onStop={stopInterview}
            onTranscriptUpdate={setTranscript}
            onAnswerComplete={handleAnswerComplete}
            canProceed={currentQuestionIndex < questions.length}
          />
        </div>
        
        <div className="interview-right">
          <AIQuestionPanel
            currentQuestion={questions[currentQuestionIndex]}
            questionNumber={currentQuestionIndex + 1}
            totalQuestions={questions.length}
            isInterviewActive={isInterviewActive}
          />
        </div>
      </div>
    </div>
  );
}

export default InterviewPage;
