import React, { useState, useRef, useEffect } from 'react';
import { useSpeechToText } from '../hooks/useSpeechToText';
import './VideoInterview.css';

function VideoInterview({ isActive, onStart, onStop, onTranscriptUpdate, onAnswerComplete }) {
  const [stream, setStream] = useState(null);
  const [isMicOn, setIsMicOn] = useState(false);
  const [timer, setTimer] = useState(0);
  const videoRef = useRef(null);
  const timerIntervalRef = useRef(null);

  const {
    transcript,
    isListening,
    startListening,
    stopListening,
    resetTranscript
  } = useSpeechToText();

  useEffect(() => {
    onTranscriptUpdate(transcript);
  }, [transcript, onTranscriptUpdate]);

  useEffect(() => {
    if (isActive && isListening) {
      const checkAnswerComplete = setInterval(() => {
        if (transcript.trim().length > 50) {
          const lastChar = transcript[transcript.length - 1];
          if (lastChar === '.' || lastChar === '!' || lastChar === '?') {
            stopListening();
            onAnswerComplete(transcript);
            resetTranscript();
            setTimeout(() => {
              if (isActive) {
                startListening();
              }
            }, 2000);
          }
        }
      }, 1000);

      return () => clearInterval(checkAnswerComplete);
    }
  }, [transcript, isListening, isActive, stopListening, onAnswerComplete, resetTranscript, startListening]);

  const startCamera = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true
      });
      setStream(mediaStream);
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
      }
    } catch (error) {
      console.error('Error accessing camera/microphone:', error);
      alert('Please allow camera and microphone access to continue.');
    }
  };

  const stopCamera = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
  };

  const handleStart = () => {
    startCamera();
    startListening();
    setIsMicOn(true);
    onStart();
    
    timerIntervalRef.current = setInterval(() => {
      setTimer(prev => prev + 1);
    }, 1000);
  };

  const handleStop = () => {
    stopCamera();
    stopListening();
    setIsMicOn(false);
    onStop();
    
    if (timerIntervalRef.current) {
      clearInterval(timerIntervalRef.current);
    }
    setTimer(0);
    resetTranscript();
  };

  useEffect(() => {
    return () => {
      stopCamera();
      if (timerIntervalRef.current) {
        clearInterval(timerIntervalRef.current);
      }
    };
  }, []);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="video-interview">
      <div className="video-container">
        <video
          ref={videoRef}
          autoPlay
          playsInline
          muted
          className="video-preview"
        />
        {!stream && (
          <div className="video-placeholder">
            <p>Camera will appear here</p>
          </div>
        )}
      </div>

      <div className="video-controls">
        <div className="control-item">
          <div className={`mic-indicator ${isMicOn && isListening ? 'active' : ''}`}>
            <span className="mic-icon">üé§</span>
            <span>{isMicOn && isListening ? 'Listening...' : 'Mic Off'}</span>
          </div>
        </div>

        <div className="control-item">
          <div className="timer-display">
            <span className="timer-icon">‚è±Ô∏è</span>
            <span>{formatTime(timer)}</span>
          </div>
        </div>

        <div className="control-item">
          {!isActive ? (
            <button className="start-btn" onClick={handleStart}>
              Start Interview
            </button>
          ) : (
            <button className="stop-btn" onClick={handleStop}>
              Stop Interview
            </button>
          )}
        </div>
      </div>

      <div className="transcript-section">
        <h3>Live Transcript</h3>
        <textarea
          className="transcript-textarea"
          value={transcript}
          readOnly
          placeholder="Your speech will appear here..."
        />
      </div>
    </div>
  );
}

export default VideoInterview;
