import React from 'react';
import { useNavigate } from 'react-router-dom';
import './CompanySelection.css';

const companies = [
  {
    id: 'google',
    name: 'Google',
    role: 'Software Engineer',
    difficulty: 'Hard',
    logo: 'ðŸ”'
  },
  {
    id: 'microsoft',
    name: 'Microsoft',
    role: 'Full Stack Developer',
    difficulty: 'Medium',
    logo: 'ðŸªŸ'
  },
  {
    id: 'amazon',
    name: 'Amazon',
    role: 'Frontend Engineer',
    difficulty: 'Medium',
    logo: 'ðŸ“¦'
  },
  {
    id: 'meta',
    name: 'Meta',
    role: 'React Developer',
    difficulty: 'Hard',
    logo: 'ðŸ“˜'
  },
  {
    id: 'apple',
    name: 'Apple',
    role: 'iOS Developer',
    difficulty: 'Hard',
    logo: 'ðŸŽ'
  },
  {
    id: 'netflix',
    name: 'Netflix',
    role: 'Backend Engineer',
    difficulty: 'Medium',
    logo: 'ðŸŽ¬'
  }
];

function CompanySelection() {
  const navigate = useNavigate();

  const handleStartInterview = (companyId) => {
    navigate(`/interview/${companyId}`);
  };

  const getDifficultyColor = (difficulty) => {
    switch (difficulty) {
      case 'Hard':
        return '#d32f2f';
      case 'Medium':
        return '#f57c00';
      case 'Easy':
        return '#388e3c';
      default:
        return 'var(--green-muted)';
    }
  };

  return (
    <div className="company-selection">
      <div className="company-selection-header">
        <h1>Virtual AI Interview</h1>
        <p>Select a company to begin your interview</p>
      </div>
      
      <div className="company-grid">
        {companies.map((company) => (
          <div key={company.id} className="company-card">
            <div className="company-logo">{company.logo}</div>
            <h2 className="company-name">{company.name}</h2>
            <p className="company-role">{company.role}</p>
            <span 
              className="difficulty-badge"
              style={{ backgroundColor: getDifficultyColor(company.difficulty) }}
            >
              {company.difficulty}
            </span>
            <button
              className="start-interview-btn"
              onClick={() => handleStartInterview(company.id)}
            >
              Start Interview
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CompanySelection;
